import {v4 as uuid} from 'uuid';
import {db} from '../database';

export const createNewListingRoute = {
    method: 'POST',
    path: '/api/listings',
    handler: async (req, h) => {
        const id =uuid();
        const {Date='',KITREF_NO='',Month='',LENOVO_CLAIM='',Customer='',Machine_Type='',Serial_NO='',Warranty_Expiry='',Warranty_Type='',DEF_FRU='',DEF_SERIAL_NO='',REP_FRU='',REP_SNO='',Problem_Reported='',Action_Taken='',Recieved_Date='',Remarks='',CreateUserId='',CreateDate=''}= req.payload;
        const userId = '12345';
        const views = 0;

        await db.query(`
            INSERT INTO listings (id,Date,KITREF_NO,Month,LENOVO_CLAIM,Customer,Machine_Type,Serial_NO,Warranty_Expiry,Warranty_Type,DEF_FRU,DEF_SERIAL_NO,REP_FRU,REP_SNO,Problem_Reported,Action_Taken,Recieved_Date,Remarks,CreateUserId,CreateDate,user_id,views)
               VALUES(?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?); 
        `,
            [   id,
                Date,
                KITREF_NO,
                Month,
                LENOVO_CLAIM,
                Customer,
                Machine_Type,
                Serial_NO,
                Warranty_Expiry,
                Warranty_Type,
                DEF_FRU,
                DEF_SERIAL_NO,
                REP_FRU,
                REP_SNO,
                Problem_Reported,
                Action_Taken,
                Recieved_Date,
                Remarks,
                CreateUserId,
                CreateDate,
                userId,
                views]
        );

        return {id,Date,KITREF_NO,Month,LENOVO_CLAIM,Customer,Machine_Type,Serial_NO,Warranty_Expiry,Warranty_Type,DEF_FRU,DEF_SERIAL_NO,REP_FRU,REP_SNO,Problem_Reported,Action_Taken,Recieved_Date,Remarks,CreateUserId,CreateDate,user_id:userId,views};
    }
}

