sourcesContent:
import { v4 as uuid } from 'uuid';
import { db } from '../database';

export const createNewListingRoute = {
    method: 'POST',
    path: '/api/listings',
    handler: async (req, h) => {
        const id = uuid();
        const {
            KITREF_NO = '',
            Month = '',
            LENOVO_CLAIM = '',
            Customer = '',
            Machine_Type = '',
            Serial_NO = '',
            Warranty_Expiry = '',
            Warranty_Type = '',
            DEF_FRU = '',
            DEF_SERIAL_NO = '',
            REP_FRU = '',
            REP_SNO = '',
            Problem_Reported = '',
            Action_Taken = '',
            Recieved_Date='',
            Remarks = ''
        } = req.payload;

        // Set default values
        const CreateUserId = 12345; // Assuming it's an integer
        const userId = '12345';
        const views = 0;

        // Automatically set the DateField and Recieved_Date to the current date and time
        const currentDate = new Date().toISOString().slice(0, 19).replace('T', ' ');

        try {
            await db.query(`
                INSERT INTO listings (
                    id, Date, KITREF_NO, Month, LENOVO_CLAIM, Customer, Machine_Type, Serial_NO, 
                    Warranty_Expiry, Warranty_Type, DEF_FRU, DEF_SERIAL_NO, REP_FRU, REP_SNO, 
                    Problem_Reported, Action_Taken, Recieved_Date, Remarks, CreateUserId, CreateDate, 
                    user_id, views
                ) VALUES (
                    ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?,?
                ); 
            `,
                [
                    id,
                    currentDate,         // Automatically set current date
                    KITREF_NO,
                    Month,
                    LENOVO_CLAIM,
                    Customer,
                    Machine_Type,
                    Serial_NO,
                    Warranty_Expiry,
                    Warranty_Type,
                    DEF_FRU,
                    DEF_SERIAL_NO,
                    REP_FRU,
                    REP_SNO,
                    Problem_Reported,
                    Action_Taken,
                    Recieved_Date,         // Automatically set current date
                    Remarks,
                    CreateUserId,
                    currentDate,         // Automatically set current date
                    userId,
                    views
                ]
            );

            return {
                id,
                DateField: currentDate,        // Automatically set current date
                KITREF_NO,
                Month,
                LENOVO_CLAIM,
                Customer,
                Machine_Type,
                Serial_NO,
                Warranty_Expiry,
                Warranty_Type,
                DEF_FRU,
                DEF_SERIAL_NO,
                REP_FRU,
                REP_SNO,
                Problem_Reported,
                Action_Taken,
                Recieved_Date,    
                Remarks,
                CreateUserId,
                CreateDate: currentDate,       // Automatically set current date
                user_id: userId,
                views
            };
        } catch (err) {
            console.error('Error inserting data:', err.message);  // More detailed logging
            return h.response({ error: 'Failed to create listing' }).code(500);
        }
    }
};

